mapper.select:
		SELECT 
		p.PAPER_ID,p.TITLE,p.ABSTRACT,p.PFILE_URL,p.state,p.CREATE_AT,p.creator,bu1.user_nm create_Nm,
		p.send_At,p.examor,bu2.user_nm examor_Nm,p.EXAM_AT,p.EXAM_REMARK,p.EXAM_FILE_URL
    	from GPMS_PAPER p
    	left join base_user bu1 on p.creator=bu1.user_id
    	left join base_user bu2 on p.EXAMOR=bu2.user_id




						and p.creator=#{ew.entity.creator,jdbcType=NUMERIC}


						and p.TITLE like '%' || #{ew.entity.title,jdbcType=VARCHAR} || '%'


						and bu1.user_nm like '%' || #{ew.entity.createNm,jdbcType=VARCHAR} || '%'


						and bu2.user_nm like '%' || #{ew.entity.examorNm,jdbcType=VARCHAR} || '%'


						and p.state = #{ew.entity.state,jdbcType=VARCHAR}


						and to_char(p.create_At,'yyyy-mm-dd hh24:mi:ss') >= #{ew.entity.createAtStart,jdbcType=VARCHAR}


						and to_char(p.create_At,'yyyy-mm-dd hh24:mi:ss') <= #{ew.entity.createAtEnd,jdbcType=VARCHAR}


						and to_char(p.send_At,'yyyy-mm-dd hh24:mi:ss') >= #{ew.entity.sendAtStart,jdbcType=VARCHAR}


						and to_char(p.send_At,'yyyy-mm-dd hh24:mi:ss') <= #{ew.entity.sendAtEnd,jdbcType=VARCHAR}


						and to_char(p.exam_At,'yyyy-mm-dd hh24:mi:ss') >= #{ew.entity.examAtStart,jdbcType=VARCHAR}


						and to_char(p.exam_At,'yyyy-mm-dd hh24:mi:ss') <= #{ew.entity.examAtEnd,jdbcType=VARCHAR}




				${ew.sqlSegment}


		order by p.CREATE_AT,p.send_At
