mapper.select:
		SELECT sr.SR_ID,sr.TITLE,sr.REMARK,sr.FILE_URL,sr.state,sr.CREATE_AT,sr.creator,bu1.user_nm create_Nm,
		sr.MOD_AT,sr.send_At
    	from GPMS_SUBJECT_REPORT sr
    	left join base_user bu1 on sr.creator=bu1.user_id




						and sr.creator=#{ew.entity.creator,jdbcType=NUMERIC}


						and sr.TITLE like '%' || #{ew.entity.title,jdbcType=VARCHAR} || '%'


						and bu1.user_nm like '%' || #{ew.entity.createNm,jdbcType=VARCHAR} || '%'


						and sr.state = #{ew.entity.state,jdbcType=VARCHAR}


						and to_char(sr.create_At,'yyyy-mm-dd hh24:mi:ss') >= #{ew.entity.createAtStart,jdbcType=VARCHAR}


						and to_char(sr.create_At,'yyyy-mm-dd hh24:mi:ss') <= #{ew.entity.createAtEnd,jdbcType=VARCHAR}




				${ew.sqlSegment}


		order by sr.CREATE_AT,sr.send_At
