mapper.select:
SELECT u.USER_ID,u.ORG_ID,o.ORG_NM,u.USER_NM,u.LOGIN_NM,u.PASSWD,u.passwd_err,u.PHONE_NUM,u.ADDRESS,u.ip_addr,u.EMAIL, u.is_LOCK,u.sign_at,u.last_login_at,u.REMARK,u.CREATOR,u.CREATE_AT,u.MODER,u.MOD_COUNT,u.MOD_AT,u.IS_DEL FROM BASE_USER u LEFT JOIN BASE_ORG o on u.ORG_ID = o.ORG_ID WHERE u.IS_DEL='0'
and u.USER_NM like '%' || #{ew.entity.userNm,jdbcType=VARCHAR} || '%'
and u.LOGIN_NM like '%' || #{ew.entity.loginNm,jdbcType=VARCHAR} || '%'
and u.ADDRESS like '%' || #{ew.entity.address,jdbcType=VARCHAR} || '%'
and u.is_LOCK = #{ew.entity.isLock,jdbcType=VARCHAR}
and to_char(u.sign_at,'yyyy-mm-dd hh24:mi:ss') >= #{ew.entity.signAtStart,jdbcType=VARCHAR}
and to_char(u.sign_at,'yyyy-mm-dd hh24:mi:ss') <= #{ew.entity.signAtEnd,jdbcType=VARCHAR}
and u.ORG_ID in( SELECT t.ORG_ID FROM BASE_ORG t where t.IS_USE='1' START WITH t.ORG_ID= #{ew.entity.orgId,jdbcType=NUMERIC} CONNECT BY PRIOR t.ORG_ID = t.PORG_ID)
